main.script:
(function(){
    window.slickConfigs = window.slickConfigs || [];
    window.slickConfigs.push({
        // content
        selector: '.m-audience-tabs__track',
        slickConfig: {
            speed: 300,
            centerMode: true,
            centerPadding: '100px',
            slidesToShow: 1,
            slidesToScroll: 1,
            // force tab #1 to be shown on load for all users
            // initialSlide: 0, 
            arrows: false,
            dots: true,
            adaptiveHeight: true,
            appendDots: '.m-audience-tabs__nav-container',
            dotsClass: 'm-audience-tabs__nav',
            customPaging: function(slider, i) {
                var title = $(slider.$slides[i]).data('tabTitle');
                return $('').text(title);
            },
        },
        responsive: [
            {
                breakpoint: 'mobile nav',
                settings: {
                    centerMode: false,
                    centerPadding: '0px',
                },
            },
            {
                breakpoint: 'small',
                settings: {
                    centerMode: false,
                    centerPadding: '0px',
                },
            }
        ],
    });
}());

